import React from "react";

export default class Todo extends React.Component {
    constructor() {
        super();
        this.input = React.createRef()
        this.input1 = React.createRef()
        this.input2 = React.createRef()
        this.input3 = React.createRef()
        this.state = {
            list:[]
        }
    }

    addTodo = () => {
        const Item = {
          value: this.input.current.value,
          value1: this.input.current.value,
          value2: this.input.current.value,
          value3: this.input.current.value,
        };

        if (localStorage.getItem("list") === null) {
            const list = []
            list.push(Item)
            localStorage.setItem("list",JSON.stringify(list))
        }
        else {
            const list = JSON.parse(localStorage.getItem("list"))
            list.push(Item)
            localStorage.setItem("list",JSON.stringify(list))
        }

        this.setState({
            list: JSON.parse(localStorage.getItem("list"))
        })
        document.getElementById("user").value = ""

    }

    componentDidMount() {

        const list = window.localStorage.getItem("list")
        const value = JSON.parse(list)
        if (list === null) {
            return false
        }
        else {
            this.setState({
                list : value
            })
        }
    }

    delete = () => {
        const list1 = JSON.parse(localStorage.getItem("list"))
        list1.splice(0, 1)
        this.setState({ list: list1 })
        localStorage.setItem("list", JSON.stringify(list1))

    }

    render() {
        return (
            <div>
                <input id="user" type="text" ref={this.input} placeholder="FullName" />
                <input type="text" ref={ this.input1} placeholder="City" />
            </div>
        )
    }
}
